import React, { useState } from "react";
import "../css/DietForm.css";

function DietForm() {
  const [formData, setFormData] = useState({
    name: "",
    surname: "",
    gender: "",
    height: "",
    age: "",
    weight: "",
    targetWeight: "",
    duration: "7"
  });

  const [loading, setLoading] = useState(false); // Add loading state

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Start loading

    try {
      const response = await fetch("http://localhost:5000/generate-diet", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = "diet-plan.pdf";
      a.click();
    } catch (error) {
      console.error("Error generating diet plan:", error);
    } finally {
      setLoading(false); // Stop loading
    }
  };

  return (
    <div>
      <div>
        <h3 style={{ fontSize: 35, paddingLeft: 10 }}>Fit Diet Program</h3>
      </div>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
        <input name="surname" placeholder="Surname" value={formData.surname} onChange={handleChange} required />
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <select name="gender" value={formData.gender} onChange={handleChange} style={{ marginRight: '10px' }}>
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
          <input name="age" type="number" placeholder="Age" value={formData.age} onChange={handleChange} required />
        </div>

        <input name="height" type="number" placeholder="Height (cm)" value={formData.height} onChange={handleChange} required />
        <input name="weight" type="number" placeholder="Weight (kg)" value={formData.weight} onChange={handleChange} required />
        <input name="targetWeight" type="number" placeholder="Target Weight (kg)" value={formData.targetWeight} onChange={handleChange} required />
        <select name="duration" value={formData.duration} onChange={handleChange}>
          <option value="7">7 Days</option>
          <option value="15">15 Days</option>
        </select>
        <button type="submit" disabled={loading}>
          {loading ? "Generating..." : "Generate Diet Plan"}
        </button>
      </form>
    </div>
  );
}

export default DietForm;
